import React from "react";
import "./about_me.css";
import { motion } from "framer-motion";

const AboutMe = () => {
  return (
    <motion.div
      initial={{
        opacity: 0,
        y: 40,
      }}
      whileInView={{
        opacity: 1,
        y: 0,
      }}
      transition={{
        delay: 0.2,
        duration: 0.5,
      }}
      viewport={{
        once: true,
      }}
      className="about_me"
      id="aboutMey"
    >
      <h2 className="heading">About Me</h2>
      <div className="about_me_info">
        <p className="about_me_left">
          Hi, I’m Saiteja — a dedicated developer with a strong interest in
          building clean, efficient, and user-friendly applications. I work with
          the MERN stack and regularly practice DSA to improve my
          problem-solving skills. I enjoy learning new technologies, working on
          real projects, and continuously improving myself as a developer. My
          goal is to grow as a full-stack developer and contribute to
          meaningful, high-impact products.
        </p>
        <div className="about_me_right"></div>
      </div>
    </motion.div>
  );
};

export default AboutMe;
